// src/Weather.js
import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';

const Weather = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);

    // After user clicks a button to subscribe
const subscribeUser = async () => {
    if ('serviceWorker' in navigator) {
      const registration = await navigator.serviceWorker.ready;
      const subscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: urlBase64ToUint8Array(publicVapidKey)
      });
      await fetch('/subscribe', {
        method: 'POST',
        body: JSON.stringify(subscription),
        headers: {
          'Content-Type': 'application/json'
        }
      });
    }
  };


    const handleInputChange = (e) => {
        setCity(e.target.value);
    };

    const fetchWeather = async (e) => {
        e.preventDefault();
        try {
            const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY; // Replace with your OpenWeatherMap API Key
            console.log('API Key:', process.env.REACT_APP_OPENWEATHER_API_KEY);

            const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
            // const url = `https://api.openweathermap.org/data/2.5/weather?id=${city}&appid=${apiKey}`
            const response = await fetch(url);
            console.log('response', response)
            const data = await response.json();
            setWeather(data);

            if (!response.ok) {
                console.error('Error from API:', data);
                throw new Error('Failed to fetch weather data'); // Or handle this error appropriately
            }
        } catch (error) {
            console.error("Error fetching weather data:", error);
        }
    };

    console.log('weather', weather)

    return (
        <Container className="p-3">
            <Row className="justify-content-md-center">
                <Col xs={12} md={6}>
                    <Form onSubmit={fetchWeather}>
                        <Form.Group controlId="cityName">
                            <Form.Label>Enter city name</Form.Label>
                            <Form.Control 
                                type="text" 
                                placeholder="City name" 
                                value={city} 
                                onChange={handleInputChange} 
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit">
                            Get Weather
                        </Button>
                    </Form>
                    {weather && (
                        <Card className="mt-3">
                            <Card.Body>
                                <Card.Title>{weather.name}</Card.Title>
                                <Card.Text>
                                    Temperature: {weather.main.temp} Â°C
                                    <br />
                                    Weather: {weather.weather[0].description}
                                    <br />
                                    Humidity: {weather.main.humidity}%
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    )}
                </Col>
            </Row>
        </Container>
    );
};

export default Weather;
